import React, { useState } from 'react';
import styled from 'styled-components';
import { FiMail, FiFacebook, FiTwitter, FiInstagram, FiYoutube } from 'react-icons/fi';

const FooterContainer = styled.footer`
  background: #1a1a1a;
  color: white;
  padding: 60px 0 30px;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const FooterContent = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr 1fr 1fr;
  gap: 40px;
  margin-bottom: 40px;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 30px;
  }
`;

const FooterSection = styled.div`
  h3 {
    font-size: 18px;
    font-weight: 700;
    margin-bottom: 20px;
    color: white;
  }

  p {
    color: #ccc;
    line-height: 1.6;
    margin-bottom: 15px;
  }
`;

const Logo = styled.div`
  font-size: 24px;
  font-weight: 800;
  color: white;
  margin-bottom: 20px;
  letter-spacing: 2px;
`;

const NewsletterForm = styled.form`
  display: flex;
  gap: 10px;
  margin-top: 20px;

  @media (max-width: 768px) {
    flex-direction: column;
  }
`;

const NewsletterInput = styled.input`
  flex: 1;
  padding: 12px 16px;
  border: 1px solid #333;
  border-radius: 6px;
  background: #2a2a2a;
  color: white;
  font-size: 14px;

  &::placeholder {
    color: #999;
  }

  &:focus {
    outline: none;
    border-color: #666;
  }
`;

const NewsletterButton = styled.button`
  background: #000;
  color: white;
  border: none;
  padding: 12px 24px;
  border-radius: 6px;
  cursor: pointer;
  font-weight: 600;
  font-size: 14px;
  transition: background-color 0.3s ease;

  &:hover {
    background: #333;
  }
`;

const FooterLinks = styled.ul`
  list-style: none;
  padding: 0;
  margin: 0;

  li {
    margin-bottom: 10px;
  }

  a {
    color: #ccc;
    text-decoration: none;
    font-size: 14px;
    transition: color 0.3s ease;

    &:hover {
      color: white;
    }
  }
`;

const SocialLinks = styled.div`
  display: flex;
  gap: 15px;
  margin-top: 20px;
`;

const SocialLink = styled.a`
  display: flex;
  align-items: center;
  justify-content: center;
  width: 40px;
  height: 40px;
  background: #333;
  border-radius: 50%;
  color: white;
  text-decoration: none;
  transition: all 0.3s ease;

  &:hover {
    background: #555;
    transform: translateY(-2px);
  }
`;

const FooterBottom = styled.div`
  border-top: 1px solid #333;
  padding-top: 30px;
  text-align: center;
  color: #999;
  font-size: 14px;
`;

const Footer = () => {
  const [email, setEmail] = useState('');

  const handleNewsletterSubmit = (e) => {
    e.preventDefault();
    if (email) {
      // Handle newsletter subscription
      console.log('Newsletter subscription:', email);
      setEmail('');
    }
  };

  return (
    <FooterContainer>
      <Container>
        <FooterContent>
          <FooterSection>
            <Logo>ECOMMERCE</Logo>
            <p>
              Temukan koleksi fashion terbaru dan terbaik untuk gaya hidup Anda. 
              Kami menyediakan pakaian berkualitas tinggi dengan desain yang trendi.
            </p>
            <NewsletterForm onSubmit={handleNewsletterSubmit}>
              <NewsletterInput
                type="email"
                placeholder="Your email here"
                value={email}
                onChange={(e) => setEmail(e.target.value)}
              />
              <NewsletterButton type="submit">Submit</NewsletterButton>
            </NewsletterForm>
            <SocialLinks>
              <SocialLink href="#" aria-label="Facebook">
                <FiFacebook size={18} />
              </SocialLink>
              <SocialLink href="#" aria-label="Twitter">
                <FiTwitter size={18} />
              </SocialLink>
              <SocialLink href="#" aria-label="Instagram">
                <FiInstagram size={18} />
              </SocialLink>
              <SocialLink href="#" aria-label="YouTube">
                <FiYoutube size={18} />
              </SocialLink>
            </SocialLinks>
          </FooterSection>

          <FooterSection>
            <h3>POPULAR</h3>
            <FooterLinks>
              <li><a href="/products?category=men">Men's Clothing</a></li>
              <li><a href="/products?category=women">Women's Clothing</a></li>
              <li><a href="/products?category=shoes">Shoes</a></li>
              <li><a href="/products?category=accessories">Accessories</a></li>
              <li><a href="/products?sale=true">Sale Items</a></li>
            </FooterLinks>
          </FooterSection>

          <FooterSection>
            <h3>VIEW ALL</h3>
            <FooterLinks>
              <li><a href="/products">All Products</a></li>
              <li><a href="/products?category=new">New Arrivals</a></li>
              <li><a href="/products?category=trending">Trending</a></li>
              <li><a href="/products?category=best-seller">Best Sellers</a></li>
              <li><a href="/products?category=limited">Limited Edition</a></li>
            </FooterLinks>
          </FooterSection>

          <FooterSection>
            <h3>INFO</h3>
            <FooterLinks>
              <li><a href="/about">About Us</a></li>
              <li><a href="/contact">Contact</a></li>
              <li><a href="/shipping">Shipping Info</a></li>
              <li><a href="/returns">Returns</a></li>
              <li><a href="/size-guide">Size Guide</a></li>
            </FooterLinks>
          </FooterSection>
        </FooterContent>

        <FooterBottom>
          <p>&copy; 2024 ECOMMERCE. All rights reserved. | Privacy Policy | Terms of Service</p>
        </FooterBottom>
      </Container>
    </FooterContainer>
  );
};

export default Footer;
