const ProductGridComponent = ({ title, subtitle, products, categories }) => {
  const [activeCategory, setActiveCategory] = useState('all');
  const [filteredProducts, setFilteredProducts] = useState(products);
  const [wishlist, setWishlist] = useState(new Set());
  const { addToCart } = useCart();

  const handleCategoryChange = (category) => {
    setActiveCategory(category);
    if (category === 'all') {
      setFilteredProducts(products);
    } else {
      setFilteredProducts(products.filter(product => product.category === category));
    }
  };

  const handleAddToCart = (product) => {
    addToCart(product);
    toast.success(`${product.name} ditambahkan ke keranjang!`, {
      style: {
        background: 'linear-gradient(135deg, #10b981 0%, #059669 100%)',
        color: 'white',
        borderRadius: '12px',
        boxShadow: '0 8px 25px rgba(16, 185, 129, 0.3)',
      },
    });
  };

  const handleWishlistToggle = (productId) => {
    const newWishlist = new Set(wishlist);
    if (newWishlist.has(productId)) {
      newWishlist.delete(productId);
      toast.success('Removed from wishlist');
    } else {
      newWishlist.add(productId);
      toast.success('Added to wishlist', {
        style: {
          background: 'linear-gradient(135deg, #ef4444 0%, #dc2626 100%)',
          color: 'white',
          borderRadius: '12px',
          boxShadow: '0 8px 25px rgba(239, 68, 68, 0.3)',
        },
      });
    }
    setWishlist(newWishlist);
  };

  const renderStars = (rating) => {
    return Array.from({ length: 5 }, (_, i) => (
      <Star key={i} filled={i < rating} />
    ));
  };

  return (
    <Section>
      <Container>
        <SectionHeader>
          <SectionTitle>{title}</SectionTitle>
          {subtitle && <SectionSubtitle>{subtitle}</SectionSubtitle>}
        </SectionHeader>

        {categories && (
          <CategoryTabs>
            <Tab 
              active={activeCategory === 'all'} 
              onClick={() => handleCategoryChange('all')}
              whileHover={{ scale: 1.05 }}
              whileTap={{ scale: 0.95 }}
            >
              All
            </Tab>
            {categories.map(category => (
              <Tab 
                key={category}
                active={activeCategory === category} 
                onClick={() => handleCategoryChange(category)}
                whileHover={{ scale: 1.05 }}
                whileTap={{ scale: 0.95 }}
              >
                {category}
              </Tab>
            ))}
          </CategoryTabs>
        )}

        <ProductGrid>
          <AnimatePresence>
            {filteredProducts.map((product, index) => (
              <ProductCard
                key={product.id}
                initial={{ opacity: 0, y: 50 }}
                animate={{ opacity: 1, y: 0 }}
                exit={{ opacity: 0, y: -50 }}
                transition={{ duration: 0.5, delay: index * 0.1 }}
                whileHover={{ scale: 1.02 }}
                layout
              >
                <ProductImage bgImage={product.image}>
                  <ProductBadges>
                    {product.originalPrice && (
                      <SaleBadge>
                        <FiTag size={10} />
                        Sale
                      </SaleBadge>
                    )}
                    {product.isNew && (
                      <NewBadge>New</NewBadge>
                    )}
                  </ProductBadges>
                  <ProductOverlay>
                    <QuickViewButton>
                      <FiEye size={16} />
                      Quick View
                    </QuickViewButton>
                  </ProductOverlay>
                  <ProductActions>
                    <ActionButton
                      className={wishlist.has(product.id) ? 'active' : ''}
                      onClick={() => handleWishlistToggle(product.id)}
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      <FiHeart size={18} />
                    </ActionButton>
                    <ActionButton 
                      onClick={() => handleAddToCart(product)}
                      whileHover={{ scale: 1.1 }}
                      whileTap={{ scale: 0.9 }}
                    >
                      <FiShoppingCart size={18} />
                    </ActionButton>
                  </ProductActions>
                </ProductImage>
                <ProductInfo>
                  <ProductName>{product.name}</ProductName>
                  <ProductPrice>
                    <CurrentPrice>${product.price}</CurrentPrice>
                    {product.originalPrice && (
                      <OriginalPrice>${product.originalPrice}</OriginalPrice>
                    )}
                  </ProductPrice>
                  <Rating>
                    {renderStars(product.rating || 4)}
                    <RatingText>({product.reviews || Math.floor(Math.random() * 100) + 10})</RatingText>
                  </Rating>
                </ProductInfo>
              </ProductCard>
            ))}
          </AnimatePresence>
        </ProductGrid>

        <div style={{ textAlign: 'center' }}>
          <ViewAllButton to="/products">
            VIEW ALL PRODUCTS
          </ViewAllButton>
        </div>
      </Container>
    </Section>
  );
};

