import React from 'react';
import { Link } from 'react-router-dom';
import styled from 'styled-components';
import { motion } from 'framer-motion';

const HeroContainer = styled.section`
  padding: 60px 0;
  background: #f8f9fa;
`;

const Container = styled.div`
  max-width: 1200px;
  margin: 0 auto;
  padding: 0 20px;
`;

const HeroGrid = styled.div`
  display: grid;
  grid-template-columns: 2fr 1fr;
  gap: 30px;
  align-items: center;

  @media (max-width: 768px) {
    grid-template-columns: 1fr;
    gap: 20px;
  }
`;

const MainBanner = styled(motion.div)`
  background: linear-gradient(135deg, #667eea 0%, #764ba2 100%);
  border-radius: 12px;
  padding: 60px 40px;
  color: white;
  position: relative;
  overflow: hidden;
  min-height: 400px;
  display: flex;
  flex-direction: column;
  justify-content: center;

  @media (max-width: 768px) {
    padding: 40px 20px;
    min-height: 300px;
  }
`;

const BannerImage = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  width: 50%;
  height: 100%;
  background: url('https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80') center/cover;
  opacity: 0.3;
  border-radius: 0 12px 12px 0;

  @media (max-width: 768px) {
    width: 100%;
    opacity: 0.2;
  }
`;

const BannerContent = styled.div`
  position: relative;
  z-index: 2;
  max-width: 400px;
`;

const BannerTitle = styled.h1`
  font-size: 48px;
  font-weight: 800;
  margin-bottom: 16px;
  line-height: 1.1;

  @media (max-width: 768px) {
    font-size: 32px;
  }
`;

const BannerSubtitle = styled.p`
  font-size: 18px;
  margin-bottom: 30px;
  opacity: 0.9;
  line-height: 1.5;

  @media (max-width: 768px) {
    font-size: 16px;
  }
`;

const BannerButton = styled(Link)`
  display: inline-block;
  background: white;
  color: #333;
  padding: 16px 32px;
  border-radius: 8px;
  text-decoration: none;
  font-weight: 600;
  font-size: 16px;
  transition: all 0.3s ease;

  &:hover {
    transform: translateY(-2px);
    box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
  }
`;

const SideBanners = styled.div`
  display: flex;
  flex-direction: column;
  gap: 20px;
`;

const SideBanner = styled(motion.div)`
  background: white;
  border-radius: 12px;
  padding: 30px;
  box-shadow: 0 4px 20px rgba(0, 0, 0, 0.1);
  position: relative;
  overflow: hidden;
  min-height: 180px;
  display: flex;
  flex-direction: column;
  justify-content: center;

  @media (max-width: 768px) {
    min-height: 150px;
    padding: 20px;
  }
`;

const SideBannerImage = styled.div`
  position: absolute;
  top: 0;
  right: 0;
  width: 40%;
  height: 100%;
  background: url(${props => props.bgImage}) center/cover;
  opacity: 0.8;
`;

const SideBannerContent = styled.div`
  position: relative;
  z-index: 2;
  max-width: 200px;
`;

const SideBannerTitle = styled.h3`
  font-size: 24px;
  font-weight: 700;
  margin-bottom: 8px;
  color: #333;

  @media (max-width: 768px) {
    font-size: 20px;
  }
`;

const SideBannerSubtitle = styled.p`
  font-size: 14px;
  color: #666;
  margin-bottom: 16px;
`;

const SideBannerButton = styled(Link)`
  display: inline-block;
  background: #000;
  color: white;
  padding: 10px 20px;
  border-radius: 6px;
  text-decoration: none;
  font-weight: 500;
  font-size: 14px;
  transition: all 0.3s ease;

  &:hover {
    background: #333;
    transform: translateY(-1px);
  }
`;

const Hero = () => {
  return (
    <HeroContainer>
      <Container>
        <HeroGrid>
          <MainBanner
            initial={{ opacity: 0, x: -50 }}
            animate={{ opacity: 1, x: 0 }}
            transition={{ duration: 0.8 }}
          >
            <BannerImage />
            <BannerContent>
              <BannerTitle>Color of Summer Outfit</BannerTitle>
              <BannerSubtitle>
                Discover the latest fashion trends and create your perfect summer look with our curated collection.
              </BannerSubtitle>
              <BannerButton to="/products?category=summer">
                VIEW COLLECTION
              </BannerButton>
            </BannerContent>
          </MainBanner>

          <SideBanners>
            <SideBanner
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.2 }}
            >
              <SideBannerImage bgImage="https://images.unsplash.com/photo-1544966503-7cc5ac882d5f?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80" />
              <SideBannerContent>
                <SideBannerTitle>Outdoor Active</SideBannerTitle>
                <SideBannerSubtitle>Perfect for your active lifestyle</SideBannerSubtitle>
                <SideBannerButton to="/products?category=active">
                  SHOP NOW
                </SideBannerButton>
              </SideBannerContent>
            </SideBanner>

            <SideBanner
              initial={{ opacity: 0, y: 50 }}
              animate={{ opacity: 1, y: 0 }}
              transition={{ duration: 0.8, delay: 0.4 }}
            >
              <SideBannerImage bgImage="https://images.unsplash.com/photo-1594633312681-425c7b97ccd1?ixlib=rb-4.0.3&ixid=M3wxMjA3fDB8MHxwaG90by1wYWdlfHx8fGVufDB8fHx8fA%3D%3D&auto=format&fit=crop&w=1000&q=80" />
              <SideBannerContent>
                <SideBannerTitle>Casual Comfort</SideBannerTitle>
                <SideBannerSubtitle>Relaxed style for everyday</SideBannerSubtitle>
                <SideBannerButton to="/products?category=casual">
                  EXPLORE
                </SideBannerButton>
              </SideBannerContent>
            </SideBanner>
          </SideBanners>
        </HeroGrid>
      </Container>
    </HeroContainer>
  );
};

export default Hero;
